import React from "react";
import { withStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import InputAdornment from "@material-ui/core/InputAdornment";
import TextField from "@material-ui/core/TextField";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import classNames from "classnames";
import MenuItem from "@material-ui/core/MenuItem";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import Icon from "@material-ui/core/Icon";
import DeleteIcon from "@material-ui/icons/Delete";
import NavigationIcon from "@material-ui/icons/Navigation";
import Fingerprint from "@material-ui/icons/Done";
import StarBorderIcon from "@material-ui/icons/StarBorder";
import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import GridListTileBar from "@material-ui/core/GridListTileBar";

import Fetch from '../script/Fetch.js';

const styles = theme => ({
  root: {
    display: "flex",
    flexWrap: "wrap"
  },
  margin: {
    margin: theme.spacing.unit
  },
  textField: {
    flexBasis: 200
  },
  fab: {
    margin: theme.spacing.unit
  },
  extendedIcon: {
    marginRight: theme.spacing.unit
  },
  gridList: {
    flexWrap: "nowrap",
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: "translateZ(0)"
  },
  title: {
    color: theme.palette.primary.light
  },
  titleBar: {
    background:
      "linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)"
  }
});

class OutlinedInputAdornments extends React.Component {
  state = {
    nome: "",
    cognome: '',
    registrationEmail: '',
    registrationPassword: '',
    loginEmail: '',
    loginPassword: '',
    showRegistrationPassword: false,
    showLoginPassword: false,
  };

  handleChange = prop => event => {
    this.setState({ [prop]: event.target.value });
  };





  render() {
    const { classes } = this.props;

    return (
      <div className={classes.root}>
        <TextField
          id="outlined-simple-start-adornment"
          className={classNames(classes.margin, classes.textField)}
          variant="outlined"
          label="NOME"
          value={this.state.nome}
          onChange={this.handleChange("nome")}
        />

        <TextField
          id="outlined-adornment-amount"
          className={classNames(classes.margin, classes.textField)}
          variant="outlined"
          label="COGNOME"
          value={this.state.cognome}
          onChange={this.handleChange("cognome")}
        />

        <TextField
          id="outlined-adornment-weight"
          className={classNames(classes.margin, classes.textField)}
          variant="outlined"
          label="EMAIL"
          value={this.state.registrationEmail}
          onChange={this.handleChange("registrationEmail")}
        />

        <TextField
          id="outlined-adornment-password"
          className={classNames(classes.margin, classes.textField)}
          variant="outlined"
          type={this.state.showRegistrationPassword ? "text" : "password"}
          label="Password"
          value={this.state.registrationPassword}
          onChange={this.handleChange("registrationPassword")}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  aria-label="Toggle password visibility"
                  onClick={this.setState(state => ({ showLoginPassword: !state.showLoginPassword }))}
                >
                  {this.state.showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            )
          }}
        />
        <Fab color="primary" aria-label="Add" className={classes.fab} onClick={()=>Fetch.registration()}>
          <AddIcon />
        </Fab>
        <TextField
          id="outlined-adornment-weight"
          className={classNames(classes.margin, classes.textField)}
          variant="outlined"
          label="EMAIL"
          value={this.state.loginEmail}
          onChange={this.handleChange("loginEmail")}
        />

        <TextField
          id="outlined-adornment-password"
          className={classNames(classes.margin, classes.textField)}
          variant="outlined"
          type={this.state.showLoginPassword ? "text" : "password"}
          label="Password"
          value={this.state.loginPassword}
          onChange={this.handleChange("loginPassword")}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <IconButton
                  aria-label="Toggle password visibility"
                  onClick={()=>this.setState(state => ({ showRegistrationPassword: !state.showRegistrationPassword }))}
                >
                  {this.state.showPassword ? <VisibilityOff /> : <Visibility />}
                </IconButton>
              </InputAdornment>
            )
          }}
        />

        <Fab color="secondary" aria-label="Edit" className={classes.fab}>
          <Fingerprint />
        </Fab>
        <GridList className={classes.gridList} cols={2.5} />
      </div>
    );
  }
}

